<!-- @desc generated by yqg-cli@0.1.12 -->

<template>

    <div class="b-nav-side">
        <nav class="nav">
            <div class="action-card">
                <div class="action-group">
                    <div class="nav-header">
                        <slot name="toggle">
                            <div class="icon-toggle" @click="onToggle">
                                <span></span>
                                <span></span>
                                <span></span>
                            </div>
                        </slot>

                        <slot name="nav-top"></slot>
                    </div>
                </div>

                <div v-if="navRoutes">
                    <div v-if="hasSearch">
                        <b-select
                            v-model="searchRoute"
                            :map="searchMap"
                            :dropdown-match-select-width="true"
                            placeholder="搜索页面"
                            @change="changeSearchRoute"/>
                    </div>
                    <div v-for="rootRoute in navRoutes" :key="rootRoute.name" class="action-group nav-wrap">
                        <!-- has children -->
                        <button
                            v-if="rootRoute.children.length"
                            type="button"
                            class="action-bar router-level-1"
                            @click="onToggleNavChildrenOpen(rootRoute)"
                        >
                            <span>
                                <template v-if="rootRoute.meta.hasIcon">
                                    <slot :name="rootRoute.name" >
                                        <i :class="rootRoute.meta.iconClass" class="router-icon"></i>
                                    </slot>
                                </template>
                                {{ getNavTitle(rootRoute) }}
                            </span>


                            <span class="toggle-icon">
                                <i v-if="rootRoute.$$open" class="b-icon-bottom"></i>
                                <i v-else class="b-icon-right"></i>
                            </span>
                        </button>

                        <!-- no child -->
                        <router-link
                            v-else
                            :to="{name: rootRoute.name}"
                            :class="{active: rootRoute.name === $route.name}"
                            :target="getTarget(rootRoute.meta.target,null)"
                            class="action-bar router-level-1"
                        >
                            <span>
                                <template v-if="rootRoute.meta.hasIcon">
                                    <slot :name="rootRoute.name" >
                                        <i :class="rootRoute.meta.iconClass" class="router-icon"></i>
                                    </slot>
                                </template>
                                {{ getNavTitle(rootRoute) }}
                            </span>
                        </router-link>

                        <!-- children -->
                        <div v-if="rootRoute.$$open">
                            <div
                                v-for="secondRoute in rootRoute.children"
                                :key="secondRoute.name"
                            >
                                <router-link
                                    :to="{name: secondRoute.name}"
                                    :class="{
                                        active: secondRoute.name === $route.name,
                                        'router-level-2-icon':rootRoute.meta.hasIcon}"
                                    :target="getTarget(secondRoute.meta.target,rootRoute.meta.target)"
                                    class="action-bar router-level-2"
                                >
                                    {{ getNavTitle(secondRoute) }}
                                </router-link>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </nav>
    </div>

</template>

<script type="text/babel">

    import {getText} from '../../util/i18n';
    import {genMapFromObjectArray as genMap} from '../../util/object-util';

    export default {
        name: 'BNavSide',

        props: {
            routes: {
                type: Array,
                required: true
            },
            target: {
                type: String,
                default: '_self'
            },
            hasSearch: {
                type: Boolean,
                default: false
            }
        },

        data() {
            const vm = this;
            const {routes, genNavRoutes, genSearchData} = vm;

            return {
                navRoutes: genNavRoutes(routes),

                searchRouteData: genSearchData(),
                searchRoute: ''
            };
        },

        computed: {
            searchMap() {
                const vm = this;
                return genMap(vm.searchRouteData, 'name', 'meta.navTitle');
            }
        },

        mounted() {
            const vm = this;
            vm.$watch('routes', () => {
                vm.refreshNavRoutes();
            });
        },

        methods: {
            onToggle() {
                this.$emit('toggle');
            },

            onToggleNavChildrenOpen(route) {
                const vm = this;

                route.$$open = !route.$$open;
                const openedMenu = vm.navRoutes.filter(router => router.$$open).map(router => router.name);
                this.$emit('on-open-change', openedMenu);
            },

            refreshNavRoutes() {
                const vm = this;
                const {routes, genNavRoutes, genSearchData} = vm;
                vm.navRoutes = genNavRoutes(routes);
                vm.searchRouteData = genSearchData();
            },

            genNavRoutes(routes) {
                const vm = this;

                const routeFilter = ({meta: {navTitle, navTitleKey} = {}}) => navTitle || navTitleKey;
                const rootRoutes = routes.filter(routeFilter);

                rootRoutes.forEach((rootRoute) => {
                    const defaultRootOpen = rootRoute.meta.defaultOpen;
                    vm.$set(rootRoute, '$$open', defaultRootOpen !== undefined ? defaultRootOpen : true);

                    const secondRoutes = rootRoute.children || [];
                    rootRoute.children = secondRoutes.filter(routeFilter);
                });

                return rootRoutes;
            },

            genSearchData() {
                const vm = this;
                const {routes, genNavRoutes} = vm;
                const baseRoutes = genNavRoutes(routes);

                const searchRouteData = baseRoutes.reduce((acc, cur) => {
                    if (!(cur.children && cur.children.length)) {
                        acc.push({...cur, level: 1});
                    } else {
                        cur.children.forEach((secondRoute) => {
                            acc.push({...secondRoute, parentRoute: cur, level: 2});
                        });
                    }
                    return acc;
                }, []);

                return searchRouteData;
            },

            changeSearchRoute(route) {
                const vm = this;
                const {$router, navRoutes, $route, searchRouteData} = vm;
                if (!route) {
                    return;
                }
                if ($route && $route.name === route) {
                    return;
                }
                $router.replace({name: route});

                const selectedRoute = searchRouteData.find(({name}) => (name === route));
                if (selectedRoute && selectedRoute.level === 2) {
                    const selectedRouteParentNode = navRoutes
                        .find(({name}) => (name === selectedRoute.parentRoute.name));
                    if (!selectedRouteParentNode.$$open) {
                        selectedRouteParentNode.$$open = true;
                        const openedMenu = vm.navRoutes.filter(router => router.$$open).map(router => router.name);
                        vm.$emit('on-open-change', openedMenu);
                    }
                }
            },

            getNavTitle(route) {
                const {meta: {navTitle, navTitleKey: key}} = route;
                return navTitle || getText({vm: this, key, defatultText: key});
            },

            getTarget(childTarget, selfTarget) {
                const vm = this;
                const {target} = vm;
                return childTarget || selfTarget || target;
            }
        }
    };

</script>
