/* eslint-disable */

export const log = (target, name, descriptor) => {
    const fn = descriptor.value;
    descriptor.value = function (...rest) {
        console.log(`这是调用方法【${name}】前打印的日志`, {rest, target, descriptor, this: this});
        fn.call(this, ...rest);
        console.log(`这是调用方法【${name}】后打印的日志`, {rest});
    };
};
